import { SourceFile } from 'typescript';
/** Represents the TypeScript file. */
export interface TsFile {
    /** Parsed content of the file */
    root: SourceFile;
    /** Name of the file. */
    fileName: string;
    /** Whether to allow local mutation. */
    allowMutation: boolean;
    /** Whether invariants are required and only while loops allowed. */
    reqInvariants: boolean;
    /** Records an error at the given node or range of text. */
    error: (message: string, start: number, end?: number) => void;
}
/**
 * Lints the contents of the given file.
 * @param file File to lint
 */
export declare const lintFile: (file: TsFile) => void;
